mapper.select:
select

from student
where student_id=#{student_id} and student_password=#{student_password}

mapper.update:
update student
set student_name = #{studentName,jdbcType=VARCHAR},
  student_department = #{studentDepartment,jdbcType=VARCHAR},
  student_password = #{studentPassword,jdbcType=VARCHAR}
where student_id = #{studentId,jdbcType=INTEGER}
